import React from 'react';
import './training.css';

import Deck from "react-poker";
const { decks } = require('cards');


const deck = new decks.StandardDeck({ jokers: 0 });
deck.shuffleAll();
const hand = deck.draw(5);

function deckRender(hand){
  let cardList = '<ul class="hand">';
  for(let i = 0; i < 5; i++){
    cardList += ("<li>" + `${hand[i].rank.shortName}` + " " + `${hand[i].suit.name}` + "</li>");

  }
  return cardList += '</ul>';
};

export function Training(){

  return (
    <div className="game-component" >Game Play goes here
        {document.write(deckRender(hand))}
    </div>
  );
}
