import React from 'react';
import ReactDOM from 'react-dom';
import Cards from 'cards';
import { directive } from 'babel-types';
import './card.css';

const { decks } = require('cards');

const deck = new decks.StandardDeck({ jokers: 2 });
deck.shuffleAll();
const hand = deck.draw(5);

function deckRender(hand){
  let cardList = '';
  for(let i = 0; i < 5; i++){
    cardList += ("<li>" + `${hand[i].rank.shortName}` + " " + `${hand[i].suit.name}` + "</li>");
    console.log(cardList);
  }
  return cardList;
};

export function Training(){

  return (
    <div className="game-component" >Game Play goes here
      ${console.log(hand)}
      <ul className="hand">
        {document.write(deckRender(hand))}
      </ul>
    </div>
  );
}
